mapper.select:
SELECT * FROM user 
WHERE username = #{username}
  AND mbti = #{mbti}
  AND age = #{age}

mapper.update:
UPDATE user
SET password = #{newPassword}
WHERE username = #{username}

mapper.insert:
INSERT INTO movie(
    userid, adult, poster_path, title, original_title, overview, release_date
) VALUES (
    #{userid}, #{adult}, #{posterPath}, #{title}, #{originalTitle}, #{overview}, #{releaseDate}
)
